parameter.value:
SELECT distinct id, refsetId, referencedComponentId FROM rf2_cr_irefset_full 
										WHERE NOT isValidComponentId_cr(referencedComponentId)
										AND NOT isValidComponentId_cr(referencedComponentId);

select * from rf2_cr_irefset_active
										group by refsetId,referencedComponentId,value1
										having count(1) > 1;
